mapper.update:
update product
set product_name = #{productName,jdbcType=VARCHAR},
  price = #{price,jdbcType=DECIMAL},
  descript = #{descript,jdbcType=VARCHAR}
where product_id = #{productId,jdbcType=INTEGER}

mapper.select:
select product_id, product_name, price, descript
from product
where product_id = #{productId,jdbcType=INTEGER}
